import React, { useState } from 'react';
import { motion } from 'framer-motion';
import VideoCard from './VideoCard';

export default function HomeContent() {
  const [message, setMessage] = useState("¡Haz clic para la magia!");

  const handleClick = () => {
    setMessage("¡Vaya, qué interactivo! ¿Verdad?");
  };

  // Datos de ejemplo para el feed de videos
  const techVideos = [
    {
      id: 1,
      url: "public/videos/MACHINE_LEARNING.mp4", // ¡Cambia esto por videos de tecnología reales!
      title: "El futuro de la IA (¡no es lo que esperas!)",
      description: "Una mirada profunda a las tendencias emergentes en inteligencia artificial y cómo cambiarán el mundo."
    },
    {
      id: 2,
      url: "https://www.youtube.com/embed/y8Kyi0WNg40", // ¡Cambia esto por videos de tecnología reales!
      title: "Desarrollo Web 2024: Lo que necesitas saber",
      description: "Explorando las últimas herramientas y frameworks que están dominando el panorama del desarrollo web."
    },
    {
      id: 3,
      url: "https://www.youtube.com/embed/xvFZjo5PgG0", // ¡Cambia esto por videos de tecnología reales!
      title: "Ciberseguridad para principiantes: Protege tu vida digital",
      description: "Consejos esenciales para mantener tus datos seguros en un mundo cada vez más conectado."
    }
  ];

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8 }}
      className="bg-white p-10 rounded-lg shadow-xl text-center max-w-4xl mx-auto"
    >
      <h1 className="text-4xl font-extrabold text-gray-900 mb-4">
        Grupos de investigación más conocidos en Colombia
      </h1>
      <p className="text-lg text-gray-700 mb-6">
        Un portal interactivo de Juan Pablo Alzate Villada y Romel Bayer.
      </p>
      <motion.button
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        onClick={handleClick}
        className="bg-blue-600 text-white font-bold py-3 px-8 rounded-full shadow-lg hover:bg-blue-700 transition duration-300 ease-in-out"
      >
        {message}
      </motion.button>

      <h2 className="text-3xl font-bold text-gray-800 mt-12 mb-6">
        Nuestro Feed de Videos Tecnológicos
      </h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {techVideos.map(video => (
          <VideoCard
            key={video.id}
            videoUrl={video.url}
            title={video.title}
            description={video.description}
          />
        ))}
      </div>

      <p className="mt-8 text-sm text-gray-500">
        (Sí, lo sé, la interactividad es subjetiva. Pero es un botón, ¡y hace cosas!)
      </p>
    </motion.div>
  );
}
